import { Http } from '../../http';
import { AdditionHttp } from './types';
import { injectAPIGroup } from '../../inject';
export const MkAdditionApi = injectAPIGroup({
    /**
     * 获取问题的追问列表
     *
     * @param {number} questionId
     * @returns
     */
    getByQuestion(questionId) {
        return AdditionHttp.get(`/problem/${questionId}/notes`);
    },
    /**
     * 根据id获取追问详情
     *
     * @param {number} questionId
     * @param {number} noteId
     * @returns
     */
    get(questionId, noteId) {
        return AdditionHttp.get(`/problem/${questionId}/${noteId}`);
    },
    /**
     * 创建追问
     *
     * @param {number} questionId
     * @param {AdditionForm} additionForm
     * @returns
     */
    create(questionId, additionForm) {
        return AdditionHttp.post(`/problem/${questionId}/notes`, additionForm);
    },
    /**
     * 更新追问
     *
     * @param {number} questionId
     * @param {number} additionId
     * @param {AdditionForm} additionForm
     * @returns
     */
    update(questionId, additionId, additionForm) {
        return AdditionHttp.put(`/problem/${questionId}/${additionId}`, additionForm);
    },
    /**
     * 删除追问
     *
     * @param {number} questionId
     * @param {number} additionId
     * @returns
     */
    delete(questionId, additionId) {
        return Http.delete(`/problem/${questionId}/${additionId}`);
    },
});
