import { AnswerNoteHttp } from './types';
import { injectAPIGroup } from '../../inject';
export const MkAnswerNoteApi = injectAPIGroup({
    /**
     * 创建回答评论
     *
     * @param {number} questionId
     * @param {number} answerId
     * @param {AnswerNoteFormParams} noteForm
     * @returns
     */
    create(questionId, answerId, noteForm) {
        return AnswerNoteHttp.post(`/problem/${questionId}/answer/${answerId}/note`, noteForm);
    },
    /**
     * 更新回答评论
     *
     * @param {number} questionId
     * @param {number} answerId
     * @param {number} noteId
     * @param {AnswerNoteFormParams} noteForm
     * @returns
     */
    update(questionId, answerId, noteId, noteForm) {
        return AnswerNoteHttp.put(`/problem/${questionId}/answer/${answerId}/note/${noteId}`, noteForm);
    },
    /**
     * 删除回答评论
     *
     * @param {number} questionId
     * @param {number} answerId
     * @param {number} noteId
     * @returns
     */
    delete(questionId, answerId, noteId) {
        return AnswerNoteHttp.delete(`/problem/${questionId}/answer/${answerId}/note/${noteId}`);
    },
});
