import { injectDefaultParams } from '../../base';
import { WhiteUserHttp } from './types';
import { injectAPIGroup } from '../../inject';
export const MkExpertApi = injectAPIGroup({
    /**
     * 向他提问-最新回答专家列表
     *
     * @param {SearchWithPaginationParams} [params]
     * @returns
     */
    search(params) {
        return WhiteUserHttp.list().get('/experts/search', {
            params: injectDefaultParams(params),
        });
    },
});
