import { MarkdownReferHttp } from './types';
import { injectAPIGroup } from '../../inject';
export const MkMarkdownApi = injectAPIGroup({
    /**
     * 文章的被引用列表
     *
     * @param {number} articleId
     * @returns
     */
    getArticleRefers(articleId) {
        return MarkdownReferHttp.get(`/article/${articleId}/refers`);
    },
    /**
     * 提问的被引用列表
     *
     * @param {number} questionId
     * @returns
     */
    getQuestionRefers(questionId) {
        return MarkdownReferHttp.get(`/problem/${questionId}/refers`);
    },
});
