import { injectDefaultParams } from '../../base';
import { RankUserHttp } from './types';
import { injectAPIGroup } from '../../inject';
export const MkRankApi = injectAPIGroup({
    /**
     * 获取码力值排行
     *
     * @param {RankType} type
     * @param {SortWithPaginationParams} [params]
     * @returns
     */
    getScoreRank(type, params) {
        return RankUserHttp.list().get(`/rank/score/${type}`, {
            params: injectDefaultParams(params),
        });
    },
    /**
     * 获取30天内最多回答用户
     *
     * @returns
     */
    getMostAnswerUser() {
        return RankUserHttp.list().get('/rank/most_answer');
    },
});
