import { Http } from '../../http';
import { injectAPIGroup } from '../../inject';
export const MkRedpacketApi = injectAPIGroup({
    /**
     * 领取红包
     * @param {RedpacketRootableType} rootableType
     * @param {number} rootableId
     * @param {number} redpacketId
     * @returns
     */
    receiveRedPacket(rootableType, rootableId, redpacketId) {
        return Http.post(`/redpacket/${rootableType}/${rootableId}/${redpacketId}`);
    },
});
