import { ISearchHttp, SimpleSearchHttp } from './types';
import { injectDefaultParams } from '../../base';
import { injectAPIGroup } from '../../inject';
export const MkSearchApi = injectAPIGroup({
    /**
     * 根据类型搜索，参数为空时，会取user的[type]数据（mk-editor使用）
     *
     * @param {{ type: SimpleSearchType, search?: string }} params
     * @returns
     */
    simpleSearchByType({ type, search }) {
        return SimpleSearchHttp.list().get('/search2', {
            params: {
                type,
                search,
            },
        });
    },
    /**
     * ISearch搜索接口
     *
     * @param {ISearchParams} params
     * @returns
     */
    iSearch(params) {
        return ISearchHttp.get('/isearch', {
            params: injectDefaultParams(Object.assign({ channel: 'web_search' }, params)),
        });
    },
});
