import { Http } from '../../http';
import { injectAPIGroup } from '../../inject';
import { CoterieExpertAdvertHttp } from '../coteries/types';
export const MkTechTreeApi = injectAPIGroup({
    getTree(id) {
        return Http.get(`/techtree/${id}/subtree`);
    },
    /**
     * 获取单条技术树的专家组
     *
     * @param {number} id
     * @returns
     */
    getTreeExperts(id) {
        return CoterieExpertAdvertHttp.list().get(`/techtree/${id}/tree_experts`);
    },
});
