import { TemplateHttp } from './types';
import { injectAPIGroup } from '../../inject';
export const MkTemplateApi = injectAPIGroup({
    /**
     * 获取模板详情
     * @param {number} id
     * @returns
     */
    get(id) {
        return TemplateHttp.get(`/template/${id}`);
    },
    /**
     * 获取全局的模板 目前只有[圈子主页、问答求助、问答讨论]
     * @param {{ status: TemplateStatus, linkableType: TemplateLinkableType }} params
     * @returns
     */
    getList(params) {
        const { linkableType, status } = params;
        return TemplateHttp.list().get('/template/query', {
            params: {
                linkable_type: linkableType,
                status,
            },
        });
    },
});
