import { NumberDTO, StringDTO, BaseTime, BooleanDTO } from '../../define/base';
import { TypeWrapper } from '../../http';
import { AdvertBase } from '../banner/types';
import { QuestionWithAnswerListItem } from '../question/types';
import { extend, optional } from '../../define/helper';
export const TopicBase = extend({
    id: NumberDTO,
    type: StringDTO,
    banner: StringDTO,
    url: StringDTO,
    title: StringDTO,
    readCount: NumberDTO,
}, BaseTime);
export const TopicBaseHttp = TypeWrapper(TopicBase);
export const Topic = extend({
    related: [QuestionWithAnswerListItem],
    status: BooleanDTO,
}, TopicBase);
export const TopicHttp = TypeWrapper(Topic);
export const TopicAdvert = extend(optional({
    related: TopicBase,
}), AdvertBase);
export const TopicAdvertHttp = TypeWrapper(TopicAdvert);
