import { Http } from '../../http';
import { injectAPIGroup } from '../../inject';
export const MkVoteApi = injectAPIGroup({
    /**
     * 投票-自定义选项
     * @param {number} id
     * @param {{ options: string[] | number[] }} { options }
     * @returns
     */
    voteByOptions(id, { options }) {
        return Http.post(`/vote/${id}/options`, null, {
            params: {
                // eslint-disable-next-line
                vote_item_options: options,
            },
        });
    },
});
