import axios from 'axios';
import { HttpBaseConfig } from '../base';
import qs from 'qs';
export const BaseAxiosConfig = {
    timeout: 1000 * 12,
    withCredentials: true,
    paramsSerializer: params => qs.stringify(params, {
        encode: true,
        // encodeValuesOnly: false,
        arrayFormat: 'brackets',
    }),
};
export function createAxiosIns(config = {}) {
    const ins = axios.create(Object.assign({}, BaseAxiosConfig, {
        baseURL: HttpBaseConfig.API_BASE,
    }, config));
    return ins;
}
