export const HttpBaseConfig = {
    API_BASE: '',
    UPLOAD_BASE: '',
    DEFAULT_PAGE_SIZE: 20,
    DEFAULT_PAGE_NO: 0,
};
/**
 * 设置Http基础配置
 * 包括后端地址/上传地址/默认分页信息
 *
 * @export
 * @param {Partial<HttpBaseConfigType>} config
 */
export function setHttpBaseConfig(config) {
    Object.assign(HttpBaseConfig, config);
}
/**
 * 获取默认的分页信息
 *
 * @export
 * @returns {PaginationParams}
 */
export function getDefaultPagination() {
    return {
        offset: HttpBaseConfig.DEFAULT_PAGE_NO,
        limit: HttpBaseConfig.DEFAULT_PAGE_SIZE,
    };
}
/**
 * 注入默认参数
 * 目前默认参数只有分页相关
 *
 * @export
 * @template T
 * @param {T} params
 * @returns {(PaginationParams & T)}
 */
export function injectDefaultParams(params) {
    return Object.assign({ offset: HttpBaseConfig.DEFAULT_PAGE_NO, limit: HttpBaseConfig.DEFAULT_PAGE_SIZE }, params);
}
