import { NumberDTO, StringDTO, BooleanDTO } from './base';
import { TypeWrapper } from '../http';
import { extend } from './helper';
export const VoteBase = {
    id: NumberDTO,
    title: StringDTO,
    type: StringDTO,
    totalCount: NumberDTO,
    totalUserCount: NumberDTO,
    curUserVote: BooleanDTO,
};
export const VoteOption = {
    id: NumberDTO,
    voteId: NumberDTO,
    content: StringDTO,
    voteCount: NumberDTO,
    curUserVote: BooleanDTO,
    voteUsernames: StringDTO,
};
export const Vote = extend(VoteBase, {
    voteItems: [VoteOption],
});
export const VoteHttp = TypeWrapper(Vote);
export const VoteRecord = {
    username: StringDTO,
    org: StringDTO,
    voteItemRecordList: [{ voteId: NumberDTO, content: StringDTO }],
};
export const VoteRecordHttp = TypeWrapper(VoteRecord);
